import React, { useState } from "react";

const NewCraftForm = () => {

const [craft, setCraft ] = useState({
    name:"",
    image:"",
    difficulty:"",
    description:"",
    notes:"",
    completed: false
})

const handleChange = (e) => {
    setCraft({
      ...craft,
      [e.target.name]: e.target.value,
    });
  };


  return (
    <div className="yarnform">
      <br />
      <div className="title">
        <h1>Add a Craft</h1>
      </div>
      <br />
      <img src="https://i.pinimg.com/originals/7f/cb/0b/7fcb0bdc8cda1d93e770cd8620ea3c51.jpg" />
      <br />
      <br />
      <form onSubmit={null}>
        <label>Craft Name:</label>
        <input name="name" onChange={handleChange} type="text" /> <br />
        <br />
        <label>Image url:</label>
        <input name="image" onChange={handleChange} type="text" /> <br />
        <br />
        <label>Difficulty:</label>
        <select onChange={handleChange} name="difficulty">
          <option value="easy">Easy 💚 </option>
          <option value="medium">Medium 🧡🧡</option>
          <option value="hard">Hard ❤️❤️❤️</option>
        </select>
        <br />
        <br />
        <label>Description:</label>
        <textarea
          rows={5}
          cols={20}
          name="description"
          onChange={null}
          type="text"
        />{" "}
        <br />
        <br/>
        <label>Notes:</label>
        <textarea
          rows={5}
          cols={20}
          name="description"
          onChange={null}
          type="text"
        />{" "}
        <br />
        <br />
        <input type="submit" />
      </form>
    </div>
  );
};

export default NewCraftForm;
