import React, { useState } from 'react'
import { useParams } from 'react-router-dom'

const EditCategory = () => {

  const [category, setCategory] = useState([])



    const handleChange = (e) => {
        setCategory({
          ...category,
          [e.target.name]: e.target.value,
        });
      };

      const handleSubmit = (e) => {
        e.preventDefault();
        const newCategory = {
          name: category.name,
          image: category.image,
          description: category.description
        }; }



    return (
    <div className="yarnform">
    <br />
    <div className='title'>
    <h1>Edit a Category</h1>
    </div>
    <br/>
    <img src='https://4.bp.blogspot.com/-oPkjRO51n24/VOe0Jo3czGI/AAAAAAAAIwY/kI7-N17-LYk/s1600/IMG_9526.JPG'/>
    <br/>
    <br/>
    <form onSubmit={null}>
      <label>Category Name:</label>
      <input name="name" onChange={handleChange} type="text" /> <br />
      <br />
      <label>Image url:</label>
      <input name="image" onChange={handleChange} type="text" /> <br />
      <br />
      <label>Description:</label>
      <input name="description" onChange={handleChange} type="text" />{" "}
      <br />
      <br />

      <input type="submit" />
    </form>
  </div>
  )
}

export default EditCategory
